// Product1.js

import React from 'react';
import Header from './Header';
import './Products.css'; // Ensure this CSS file styles the product detail page appropriately

const Product1 = ({ navigateTo }) => {
  return (
    <div className="product-detail-page">
      <Header navigateTo={navigateTo} />
      <div className="product-detail-content">
        <h2>Amika Dry Shampoo</h2>
        <img src="/product1.png" alt="Amika Dry Shampoo" className="product-detail-image" />
        <p className="product-description">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed dapibus dui nec ex fermentum...
        </p>
        <p className="product-price">$19.99</p>
        <p className="product-reviews">Reviews: 4.5/5</p>
        <button className="back-btn" onClick={() => navigateTo('products')}>Back to Products</button>
      </div>
    </div>
  );
};

export default Product1;
