// ProductDetail.js

import React from 'react';
import Header from './Header';
import './ProductDetail.css';

function ProductDetail({ product, navigateTo }) {
  return (
    <div className="product-detail">
      <Header navigateTo={navigateTo} />
      <div className="product-detail-content">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="product-info">
          <h2>{product.name}</h2>
          <p><strong>Price:</strong> {product.price}</p>
          <p><strong>Reviews:</strong> {product.reviews}</p>
          <p><strong>Category:</strong> {Array.isArray(product.category) ? product.category.join(', ') : product.category}</p>
          <p>{product.description}</p>
          <button onClick={() => navigateTo('products')}>Back to Products</button>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
