// CartSummary.js

import React from 'react';
import Header from './Header'; // Ensure you import your Header component correctly
import './CartSummary.css'; // Add CSS styling for CartSummary if needed

function CartSummary({ navigateTo }) {
  // Implement logic to fetch cart items and calculate totals if needed
  const cartItems = []; // Replace with actual cart items logic
  const totalItems = cartItems.length; // Calculate total items in cart
  const totalPrice = 0; // Replace with actual total price calculation logic

  return (
    <div className="cart-summary">
      <Header navigateTo={navigateTo} />

      <div className="cart-content">
        <h2>Cart Summary</h2>
        <p>Total Items: {totalItems}</p>
        <p>Total Price: ${totalPrice.toFixed(2)}</p>
        {/* Render cart items */}
        <div className="cart-items">
          {/* Map through cartItems array to display each item */}
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <img src={item.image} alt={item.name} className="cart-item-image" />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Price: ${item.price}</p>
                <p>Quantity: {item.quantity}</p>
                {/* Add functionality to remove items from cart if needed */}
              </div>
            </div>
          ))}
        </div>

        <button className="checkout-btn">Proceed to Checkout</button>
      </div>
    </div>
  );
}

export default CartSummary;
