// Concerns.js (or your parent component where DrynessConcern.js is used)

import React, { useState } from 'react';
import Product12 from './Product12'; // Import your Product12 component
import DrynessConcern from './DrynessConcern'; // Adjust the path as per your file structure


const Concerns = () => {
  const [currentView, setCurrentView] = useState(null);

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  // Render different components based on currentView state
  if (currentView === 'product12') {
    return <Product12 />;
  }

  // Render concerns page with DrynessConcern and other concerns
  return (
    <div className="concerns-page">
      <h2>Choose Your Concern</h2>
      <div className="concerns-grid">
        {/* Render other concerns components here */}
        <DrynessConcern onViewChange={handleViewChange} />
        {/* Other concerns components */}
      </div>
    </div>
  );
};

export default Concerns;
