import React, { useState } from 'react';
import Header from './Header';
import './Products.css';

const productsData = [
  { id: 1, name: 'Amika Dry Shampoo', price: '$19.99', image: '/product1.png', reviews: '4.5/5', category: 'sprays-oils' },
  { id: 2, name: 'Amika Nutrient Shampoo', price: '$24.99', image: '/product2.png', reviews: '5/5', category: 'shampoos' },
  { id: 3, name: 'Amika Nutrient Conditioner', price: '$24.99', image: '/product3.png', reviews: '4.8/5', category: 'conditioners' },
  // Add more products
];

function Products({ navigateTo }) {
  const [filter, setFilter] = useState(''); // State to store the current filter category

  const handleFilter = (category) => {
    setFilter(category); // Update the filter state when a filter is selected
  };

  const filteredProducts = filter ? productsData.filter(product => product.category.includes(filter)) : productsData;

  return (
    <div className="products">
      <Header navigateTo={navigateTo} />

      <div className="products-content">
        <h2>PRODUCTS</h2>

        <div className="filters">
          <h3>Filter by Category:</h3>
          <ul>
            <li className={filter === '' ? 'active' : ''} onClick={() => handleFilter('')}>All</li>
            <li className={filter === 'accessories' ? 'active' : ''} onClick={() => handleFilter('accessories')}>Accessories</li>
            <li className={filter === 'conditioners' ? 'active' : ''} onClick={() => handleFilter('conditioners')}>Conditioners</li>
            {/* Add more filter options as needed */}
          </ul>
        </div>

        <div className="products-grid">
          {filteredProducts.map((product) => (
            <div key={product.id} className="product-item">
              <img src={product.image} alt={product.name} className="product-image" />
              <div className="product-details">
                <h3>{product.name}</h3>
                <p className="product-price">{product.price}</p>
                <p className="product-reviews">Reviews: {product.reviews}</p>
                <button className="add-to-cart-btn">ADD TO CART</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Products;
