// Header.js
import React, { useState } from 'react';
import './Header.css';

function Header({ navigateTo }) {
  const [showConcernDropdown, setShowConcernDropdown] = useState(false);

  const handleCartClick = () => {
    // Navigate to cart page or perform necessary action
    console.log('Navigate to cart page');
  };

  const toggleConcernDropdown = () => {
    setShowConcernDropdown(!showConcernDropdown);
  };

  const handleConcernClick = (concern) => {
    // Handle click on concern option
    console.log(`Navigate to ${concern} page`);
    // Implement navigation logic as needed
  };

  return (
    <>
      <div className="announcement-bar">
        Free Shipping on Orders over $50
      </div>
      <header className="home-header">
        <a href="/" className="home-title-link">
          <h1 onClick={() => navigateTo('home')}>HAIR HAVEN</h1>
        </a>
        <nav className="home-menu">
          <ul>
            <li><a href="#products" onClick={() => navigateTo('products')}>PRODUCTS</a></li>
            <li>
              <div className="dropdown">
                <a href="#shop-by-concern" onClick={toggleConcernDropdown}>SHOP BY CONCERN</a>
                {showConcernDropdown && (
                  <div className="dropdown-content">
                    <a href="#" onClick={() => handleConcernClick('Bleached Hair')}>Bleached Hair</a>
                    <a href="#" onClick={() => handleConcernClick('Curly Hair')}>Curly Hair</a>
                    <a href="#" onClick={() => handleConcernClick('Damaged Hair')}>Damaged Hair</a>
                    <a href="#" onClick={() => handleConcernClick('Dry Hair')}>Dry Hair</a>
                    <a href="#" onClick={() => handleConcernClick('Eco-Friendly Products')}>Eco-Friendly Products</a>
                    <a href="#" onClick={() => handleConcernClick('Frizzy Hair')}>Frizzy Hair</a>
                  </div>
                )}
              </div>
            </li>
            <li><a href="#shop-by-hair-type">SHOP BY HAIR TYPE</a></li>
            <li><a href="#quiz">QUIZ</a></li>
          </ul>
        </nav>
        <div className="cart-icon" onClick={handleCartClick}>
          <img src="/cart-icon.png" alt="Cart" />
        </div>
      </header>
    </>
  );
}

export default Header;
