// ProductDetail.js

import React from 'react';
import { useParams } from 'react-router-dom';
import productsData from './productsData'; // Assuming productsData is your array of product information

function ProductDetail() {
  const { productId } = useParams(); // Get productId from URL parameter
  const product = productsData.find(product => product.id === parseInt(productId));

  if (!product) {
    return <div>Product not found.</div>;
  }

  return (
    <div className="product-detail">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>Price: {product.price}</p>
      <p>Reviews: {product.reviews}</p>
      <p>Description: {product.description}</p>
      {/* Add more details as needed */}
    </div>
  );
}

export default ProductDetail;
