import React from 'react';
import Header from './Header'; // Assuming Header.js is in the same folder
import './Quiz.css'; // Assuming you have a Quiz.css file for styling

const Quiz = () => {
  const scrollToSection = () => {
    const scrollOptions = {
      top: window.innerHeight, // Adjust as needed to scroll down by window height
      behavior: 'smooth' // Smooth scrolling behavior
    };
    window.scrollTo(scrollOptions);
  };

  return (
    <div>
      <Header />
      <div className="quiz-page" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/quiz.png)` }}>
        {/* Your quiz content goes here */}
        <button className="scroll-down-link" onClick={scrollToSection}>Scroll Down</button>
        {/* Add your quiz content here */}
        <div style={{ height: '100vh' }}>Quiz Content Here</div>
        {/* Example section to scroll down to */}
        <div id="scroll-down-section" style={{ height: '100vh', backgroundColor: '#f0f0f0' }}>
          <h2>This is the Scroll Down Section</h2>
          <p>Add your content here...</p>
        </div>
      </div>
    </div>
  );
};

export default Quiz;
