import React, { useState, useEffect } from 'react';
import Header from './Header';
import './Home.css';

const bannerImages = [
  '/banner1.jpg',
  '/banner2.jpg',
  '/banner3.jpg'
];

function Home({ navigateTo }) {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    let intervalId;
    if (!isPaused) {
      intervalId = setInterval(() => {
        setActiveIndex(prevIndex => (prevIndex + 1) % bannerImages.length);
      }, 3000); // Change banner every 3 seconds
    }
    return () => clearInterval(intervalId);
  }, [isPaused]);

  const togglePaused = () => {
    setIsPaused(prevPaused => !prevPaused);
  };

  return (
    <div className="home">
      <div className="announcement-bar">
        <p>Free Shipping on Orders over $50</p>
      </div>
      <div className="home-header">
        <Header navigateTo={navigateTo} />
        <h1 className="home-title"><a className="home-title-link" href="/">HAIR HAVEN</a></h1>
        <div className="home-menu">
          <ul>
            <li><a href="/">PRODUCTS</a></li>
            <li><a href="/">SHOP BY CONCERN</a></li>
            <li><a href="/">SHOP BY HAIR TYPE</a></li>
            <li><a href="/">QUIZ</a></li>
          </ul>
        </div>
        <div className="cart-icon">
          <img src="/cart.png" alt="Cart" />
        </div>
      </div>
      <div className="home-banner">
        {bannerImages.map((image, index) => (
          <div key={index} className={`banner-item ${index === activeIndex ? 'active' : ''}`}>
            <img src={image} alt={`Banner ${index + 1}`} />
          </div>
        ))}
        <div className="banner-navigation">
          {bannerImages.map((_, index) => (
            <div
              key={index}
              className={`circle ${index === activeIndex ? 'active' : ''}`}
              onClick={() => setActiveIndex(index)}
            ></div>
          ))}
        </div>
        <button className="pause-button" onClick={togglePaused}>
          {isPaused ? 'Play' : 'Pause'}
        </button>
      </div>
    </div>
  );
}

export default Home;
