// ProductDetail.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

const productsData = [
  // Your product data array
];

function ProductDetail() {
  const { productId } = useParams(); // Get the productId from URL params
  const product = productsData.find(product => product.id.toString() === productId);

  if (!product) {
    return <div className="product-detail">Product not found</div>;
  }

  return (
    <div className="product-detail">
      <img src={product.image} alt={product.name} className="product-image" />
      <div className="product-info">
        <h2>{product.name}</h2>
        <p className="product-price">{product.price}</p>
        <p className="product-reviews">Reviews: {product.reviews}</p>
        <p className="product-description">Product description goes here...</p>
        {/* Add more details as needed */}
      </div>
    </div>
  );
}

export default ProductDetail;
