// Header.js
import React, { useState } from 'react';
import './Header.css';

function Header({ navigateTo }) {
  const [showConcernMenu, setShowConcernMenu] = useState(false);
  const [showHairTypeMenu, setShowHairTypeMenu] = useState(false);

  const toggleConcernMenu = () => {
    setShowConcernMenu(!showConcernMenu);
  };

  const toggleHairTypeMenu = () => {
    setShowHairTypeMenu(!showHairTypeMenu);
  };

  return (
    <header>
      <div className="top-bar">
        Free Shipping on Orders over $50
      </div>
      <div className="header-main">
        <div className="header-logo" onClick={() => navigateTo('home')}>
          HAIR HAVEN
        </div>
        <div className="header-menu">
          <div className="menu-item" onClick={() => navigateTo('products')}>PRODUCTS</div>
          <div className="menu-item" onClick={toggleConcernMenu}>
            SHOP BY CONCERN
            {showConcernMenu && (
              <div className="dropdown-menu">
                <div className="dropdown-item" onClick={() => navigateTo('bleached-hair')}>Bleached Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('curly-hair')}>Curly Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('damaged-hair')}>Damaged Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('dry-hair')}>Dry Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('eco-friendly')}>Eco-Friendly Products</div>
                <div className="dropdown-item" onClick={() => navigateTo('frizzy-hair')}>Frizzy Hair</div>
              </div>
            )}
          </div>
          <div className="menu-item" onClick={toggleHairTypeMenu}>
            SHOP BY HAIR TYPE
            {showHairTypeMenu && (
              <div className="dropdown-menu">
                <div className="dropdown-item" onClick={() => navigateTo('curly-hair')}>Curly Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('fine-hair')}>Fine Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('thick-hair')}>Thick Hair</div>
                <div className="dropdown-item" onClick={() => navigateTo('straight-hair')}>Straight Hair</div>
              </div>
            )}
          </div>
          <div className="menu-item" onClick={() => navigateTo('cart')}>
            <i className="fas fa-shopping-cart"></i>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
