import React from 'react';
import './Cart.css'; // Import your CSS file for cart styling

function Cart({ cartItems, closeCart }) {
  const getTotalItems = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + (parseFloat(item.price.slice(1)) * item.quantity), 0).toFixed(2);
  };

  return (
    <div className="cart-overlay">
      <div className="cart">
        <span className="close-btn" onClick={closeCart}>&times;</span>
        <h2>Cart Summary</h2>
        <div className="cart-items">
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <img src={item.image} alt={item.name} className="cart-item-image" />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Price: {item.price} each</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="cart-summary">
          <p>Total Items: {getTotalItems()}</p>
          <p>Total Price: ${getTotalPrice()}</p>
        </div>
        <button className="checkout-btn">CHECKOUT</button>
      </div>
    </div>
  );
}

export default Cart;
