function Products({ navigateTo }) {
  const [activeCategory, setActiveCategory] = useState(null);

  const categories = [
    'shampoos',
    'conditioners',
    'masks-creams',
    'sprays-oils',
    'eco-friendly',
    'hot-tools',
    'accessories'
  ];

  const filterProducts = (category) => {
    setActiveCategory(category);
    // Filter products based on category
    // Example: This will log filtered products to the console
    const filteredProducts = productsData.filter(product => {
      return product.category.includes(category);
    });
    console.log(filteredProducts);
    // Implement logic to update displayed products based on filteredProducts
  };

  return (
    <div className="products">
      <Header navigateTo={navigateTo} />

      <div className="products-content">
        <div className="filters">
          <h3>Filter by Category</h3>
          <ul>
            {categories.map((category, index) => (
              <li
                key={index}
                className={category === activeCategory ? 'active' : ''}
                onClick={() => filterProducts(category)}
              >
                {category.toUpperCase()}
              </li>
            ))}
          </ul>
        </div>

        <h2>PRODUCTS</h2>

        <div className="products-grid">
          {productsData.map((product) => (
            <div key={product.id} className="product-item" onClick={() => navigateTo(`product${product.id}`)}>
              <img src={product.image} alt={product.name} className="product-image" />
              <div className="product-details">
                <h3>{product.name}</h3>
                <p className="product-price">{product.price}</p>
                <p className="product-reviews">Reviews: {product.reviews}</p>
                <button className="add-to-cart-btn">ADD TO CART</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Products;
