import React, { useState } from 'react';
import './Header.css'; // Add styling as needed

function Header({ navigateTo }) {
  const [showConcernMenu, setShowConcernMenu] = useState(false);
  const [showHairTypeMenu, setShowHairTypeMenu] = useState(false);

  const handleCartClick = () => {
    // Navigate to cart page or perform necessary action
    console.log('Navigate to cart page');
  };

  const toggleConcernMenu = () => {
    setShowConcernMenu(!showConcernMenu);
    setShowHairTypeMenu(false); // Close hair type menu if open
  };

  const toggleHairTypeMenu = () => {
    setShowHairTypeMenu(!showHairTypeMenu);
    setShowConcernMenu(false); // Close concern menu if open
  };

  const handleConcernClick = (concern) => {
    // Handle click on a concern option, e.g., navigate or filter products
    console.log(`Selected concern: ${concern}`);
    navigateTo('bleachedHairProducts'); // Navigate to the new page for bleached hair products
    toggleConcernMenu(); // Close the menu after selection (if needed)
  };

  const handleHairTypeClick = (hairType) => {
    // Handle click on a hair type option, e.g., navigate or filter products
    console.log(`Selected hair type: ${hairType}`);
    // Implement your logic here, such as filtering products
    // For now, let's log the selection
    toggleHairTypeMenu(); // Close the menu after selection (if needed)
  };

  return (
    <>
      <div className="announcement-bar">
        Free Shipping on Orders over $50
      </div>
      <header className="home-header">
        <a href="/" className="home-title-link">
          <h1 onClick={() => navigateTo('home')}>HAIR HAVEN</h1>
        </a>
        <nav className="home-menu">
          <ul>
            <li><a href="#products" onClick={() => navigateTo('products')}>PRODUCTS</a></li>
            <li>
              <div className="dropdown">
                <a href="#shop-by-concern" onClick={toggleConcernMenu}>SHOP BY CONCERN</a>
                {showConcernMenu && (
                  <div className="dropdown-content">
                    <a href="#bleached-hair" onClick={() => handleConcernClick('Bleached Hair')}>Bleached Hair</a>
                    {/* Add other concern options here */}
                  </div>
                )}
              </div>
            </li>
            <li>
              <div className="dropdown">
                <a href="#shop-by-hair-type" onClick={toggleHairTypeMenu}>SHOP BY HAIR TYPE</a>
                {showHairTypeMenu && (
                  <div className="dropdown-content">
                    <a href="#curly-hair-type" onClick={() => handleHairTypeClick('Curly Hair')}>Curly Hair</a>
                    {/* Add other hair type options here */}
                  </div>
                )}
              </div>
            </li>
            <li><a href="#quiz">QUIZ</a></li>
          </ul>
        </nav>
        <div className="cart-icon" onClick={handleCartClick}>
          <img src="/cart-icon.png" alt="Cart" />
        </div>
      </header>
    </>
  );
}

export default Header;
